import "./App.css";
import { Routes, Route, Link } from "react-router-dom";
import axios from "axios";
import React, { useEffect,useState } from "react";
import "bootstrap/dist/css/bootstrap.min.css";


function App() {
  const [movie, setMovie] = useState("");

  const getMovie = () => {

    axios
      .get(
        "https://api.themoviedb.org/3/movie/popular?api_key=1bfa430aada4409bfa6a3c5528128e8a"
      )

      .then((response) => {
      /*   console.log(response.data.results); */
        setMovie(response.data.results);
      })

      .catch((err) => {
        throw err;
      });
  };
  useEffect(() => {
    getMovie();
  }, []);


  const addToFavList = (element) => {
    let favList;
    localStorage.getItem("favList")
      ? (favList = JSON.parse(localStorage.getItem("favList")))
      : (favList = []);
    favList.push(element);
    localStorage.setItem("favList", JSON.stringify(favList));
  };





  return (
    <div className="App">
      <div className="movies">
        {movie &&
          movie.map((element, index) => {
            return (
              <div key={index}>


                <img src={`https://image.tmdb.org/t/p/w370_and_h556_bestv2/${element.poster_path}` } />         
                <p onClick = {()=>{console.log(element.original_title) }} >     {element.original_title}</p>
                <p> {element.overview}</p>
                <p> Release Date : {element.release_date}</p>
                <p>Rate: {element.vote_average}</p>
 <button onClick = {()=>{ }}>add</button>
              </div>
            );
          })}
      </div>
    </div>
  );
}

export default App;
