import "./App.css";
import { Routes, Route, Link, useNavigate } from "react-router-dom";
import axios from "axios";
import React, { useEffect, useState } from "react";
import "bootstrap/dist/css/bootstrap.min.css";
import MovieInfo from "./components/movie";
import Home from "./components/home";
function App() {
  const navigate = useNavigate();

  const movieDetails = (element) => {
    console.log(element);

    navigate(`/details/${element.id}`) 
  };

  const [movie, setMovie] = useState("");

  const getMovie = () => {
    axios
      .get(
        "https://api.themoviedb.org/3/movie/popular?api_key=1bfa430aada4409bfa6a3c5528128e8a"
      )

      .then((response) => {
        console.log(response.data.results);
        setMovie(response.data.results);
      })

      .catch((err) => {
        throw err;
      });
  };
  useEffect(() => {
    getMovie();
  }, []);

  return (
    <div className="App">
      <Routes>
        <Route path="/details" element={<MovieInfo />} />
      </Routes>


    </div>
  );
}

export default App;
