import { useParams } from "react-router-dom";
import React, { useState, useEffect } from "react";
import axios from "axios";
import "./style.css";
import { useNavigate } from "react-router-dom";



 const MovieInfo = () => {
 
  const navigate = useNavigate();
  const [info, setInfo] = useState([]);
  let { id } = useParams();

 

  ////////////////////////////////
//getMoviebyId
  const getMoviebyId = () => {
    axios
      .get(`https://api.themoviedb.org/3/movie/popular?api_key=1bfa430aada4409bfa6a3c5528128e8a/${id}` )
      .then((result) => {
        setInfo(result.data.doctors[0]);
      })
      .catch((err) => {
        console.log(err);
      });
  };
  useEffect(() => {
    getbyId();
  }, []);

 



  return (
    <div className="contDiv">
      <div>
        <img src={info.img} className="docinfoImg" />
      </div>
     
      <div className="allDet">
        <p className="ifo">firstName: {info.firstName}</p>

        <p  className="ifo">lastName: {info.lastName}</p>
        <p id="des" className="ifo">description: {info.description}</p>

        <p  className="ifo">category: {info.category}</p>
      </div>

      {/*       <Example /> */}

      <div className="addart">
        <p> createAppointment</p>

        <input
          type={"date"}
          placeholder={"avilabledate"}
          onChange={(e) => {
            setAvailableDate(e.target.value);
            console.log(e.target.value);
          }}
        />
        <br />
        <select
          placeholder={"IsBooked"}
          onChange={(e) => {
            setIsBooked(e.target.value);
          }}
        >
          Booked
          <option value="Select availabilty"></option>
          <option value="true">True</option>
          <option value="false">False</option>
        </select>

        <br />

        <button onClick={creatNewAppointment}>Create New appointment</button>
        <h2 className="msg">{message}</h2>

        {appointment &&
          appointment.map((elemnt, index) => {
            return (
              <div className="booking" key={index}>
                <div className="boo"> availabilty:{elemnt.availableDate}</div>
                <div  className="boo">isBooked :{`${elemnt.isBooked}`}</div>
                <br></br>
                <button
                  onClick={(e) => {
                    console.log(elemnt);
                    book(elemnt._id);
                  }}
                >
                  Book
                </button>
              </div>
            );
          })}
      </div>
      <div className="clickDiv">
        <button
          onClick={() => {
            navigate(-1);
          }}
        >
          Back
        </button>
      </div>

    </div>
  );
};

export default MovieInfo;
