import "./App.css";
import { Routes, Route, Link } from "react-router-dom";
import axios from "axios";
import React, { useEffect,useState } from "react";
import "bootstrap/dist/css/bootstrap.min.css";
import MovieInfo from "./components/movie";

function App() {
  const [movie, setMovie] = useState("");

  const getMovie = () => {

    axios
      .get(
        "https://api.themoviedb.org/3/movie/popular?api_key=1bfa430aada4409bfa6a3c5528128e8a"
      )

      .then((response) => {
        console.log(response.data.results); 
        setMovie(response.data.results.id);
      })

      .catch((err) => {
        throw err;
      });
  };
  useEffect(() => {
    getMovie();
  }, []);






  return (
    <div className="App">
      <div className="movies">
        {movie &&
          movie.map((element, index) => {
            return (
              <div key={index}>


                <img src={`https://image.tmdb.org/t/p/w370_and_h556_bestv2/${element.poster_path}` } />         

                <p> {element.overview}</p>
                <p> Release Date : {element.release_date}</p>
                <p>Rate: {element.vote_average}</p>

              </div>
            );
          })}
      </div>


      <Routes>
        <Route path="/details" element={MovieInfo} />
        <Route path="/doctors" element={<Doctors />} />

        </Routes> 


    </div>
  );
}

export default App;
