import { Routes, Route, Link, useNavigate } from "react-router-dom";
import axios from "axios";
import React, { useEffect, useState } from "react";
import "bootstrap/dist/css/bootstrap.min.css";
import "./style.css";

function Home() {
  const navigate = useNavigate();

  const movieDetails = (element) => {
    console.log(element);

    navigate(`/details/${element.id}`) 
  };

  const [movie, setMovie] = useState("");
  const [load ,setLoad]=useState("");
  const[ page ,setPage]=useState(2);

  const getMovie = () => {
    axios
      .get(
        "https://api.themoviedb.org/3/movie/popular?api_key=1bfa430aada4409bfa6a3c5528128e8a"
      )

      .then((response) => {
        console.log(response.data.results);
        setMovie(response.data.results);
      })

      .catch((err) => {
        throw err;
      });
  };

///////////////////////////////

  const getMore = () => {
    setPage(page + 1);
      axios
      .post(
        `https://api.themoviedb.org/3/movie/popular?api_key=1bfa430aada4409bfa6a3c5528128e8a&page=${page}`
      )

      .then((response) => {
        console.log(response.data.results);
        setLoad(response.data.results);
        setMovie([...movie,...load])
      })

      .catch((err) => {
        throw err;
      });
  };





  useEffect(() => {
    getMovie();
     /* getMore()  */
  }, []);


 


  return (
    <div className="App">
     
      <div className="movies">
        {movie &&
          movie.map((element, index) => {
            return (
              <div key={index}>
                <img
                  src={`https://image.tmdb.org/t/p/w370_and_h556_bestv2/${element.poster_path}`}
                />

                <p className="detail"
                  onClick={() => {
                    movieDetails(element);
                  }}
                >
                
                  {element.original_title}
                </p>
                <p> {element.overview}</p>
                <p> Release Date : {element.release_date}</p>
                <p>Rate: {element.vote_average}</p>
              </div>
            );
          })}
      </div>
      <button  onClick={() => {
                    getMore();
                  }}>Load More</button>
    </div>
  );
}
export default Home;
